	function payment_button($params) {
		global $billic, $db;
		$html = '';
		if (get_config('okpay_account') == '') {
			return $html;
		}
		if ($billic->user['verified'] == 0 && get_config('okpay_require_verification') == 1) {
			return 'verify';
		} else {
			$html.= '<form  method="post" action="https://www.okpay.com/process.html">';
			$html.= '<input type="hidden" name="ok_receiver" value="' . get_config('okpay_account') . '"/>';
			$html.= '<input type="hidden" name="ok_item_1_name" value="' . get_config('billic_companyname') . ' - Invoice #' . $params['invoice']['id'] . '">';
			$html.= '<input type="hidden" name="ok_currency" value="' . get_config('billic_currency_code') . '">';
			$html.= '<input type="hidden" name="ok_item_1_type" value="service">';
			$html.= '<input type="hidden" name="ok_item_1_price" value="' . $params['charge'] . '">';
			$html.= '<input type="hidden" name="ok_item_1_tax" value="' . $params['vat'] . '">';
			$html.= '<input type="hidden" name="ok_invoice" value="' . $params['invoice']['id'] . '">';
			$html.= '<input type="hidden" name="ok_return_success" value="http' . (get_config('billic_ssl') == 1 ? 's' : '') . '://' . get_config('billic_domain') . '/User/Invoices/ID/' . $params['invoice']['id'] . '/Status/Complete/">';
			$html.= '<input type="hidden" name="ok_return_fail" value="http' . (get_config('billic_ssl') == 1 ? 's' : '') . '://' . get_config('billic_domain') . '/User/Invoices/ID/' . $params['invoice']['id'] . '/Status/Cancelled/">';
			$html.= '<input type="hidden" name="ok_ipn" value="http' . (get_config('billic_ssl') == 1 ? 's' : '') . '://' . get_config('billic_domain') . '/Gateway/OKPay/">';
			$html.= '<input type="submit" class="btn btn-default" name="submit" value="OKPay">' . PHP_EOL;
			$html.= '</form>';
		}
		return $html;
	}
	function payment_callback() {
		global $billic, $db;
		if ($_POST['ok_txn_status'] != 'completed') {
			return 'ok_txn_status != completed';
		}
		if ($_POST['ok_receiver_email'] != get_config('okpay_email')) {
			return 'OKPay receiver email invalid: ' . $_POST['ok_receiver_email'];
		}
		// post back to OKPay system to validate
		$req = 'ok_verify=true';
		foreach ($_POST as $key => $value) {
			$req.= '&' . $key . '=' . urlencode(stripslashes($value));
		}
		$header = "POST /ipn-verify.html\r\n";
		$header.= "Content-Type: application/x-www-form-urlencoded\r\n";
		$header.= "Content-Length: " . strlen($req) . "\r\n\r\n";
		$fp = fsockopen('ssl://www.okpay.com', 443, $errno, $errstr, 30);
		if (!$fp) {
			return 'HTTP Error (fsockopen)';
		} else {
			@fputs($fp, $header . $req);
			while (!feof($fp)) {
				$res = fgets($fp, 1024);
				if (strcmp($res, 'VERIFIED') == 0) {
					if ($_POST['ok_txn_kind'] == 'payment_link') {
						$billic->module('Invoices');
						return $billic->modules['Invoices']->addpayment(array(
							'gateway' => 'OKPay',
							'invoiceid' => $_POST['ok_invoice'],
							'amount' => $_POST['ok_txn_gross'],
							'currency' => $_POST['ok_txn_currency'],
							'transactionid' => $_POST['ok_txn_id'],
						));
					} else {
						return 'Unhandled notification: ' . $_POST['ok_txn_kind'];
					}
				} else if (strcmp($res, 'INVALID') == 0) {
					return 'invalid IPN request';
				}
			}
			fclose($fp);
		}
		return 'connection error';
	}
	function settings($array) {
		global $billic, $db;
		if (empty($_POST['update'])) {
			echo '<form method="POST"><input type="hidden" name="billic_ajax_module" value="OKPay"><table class="table table-striped">';
			echo '<tr><th>Setting</th><th>Value</th></tr>';
			echo '<tr><td>Require Verification</td><td><input type="checkbox" name="okpay_require_verification" value="1"' . (get_config('okpay_require_verification') == 1 ? ' checked' : '') . '></td></tr>';
			echo '<tr><td>OKPay Account Number</td><td><input type="text" class="form-control" name="okpay_account" value="' . safe(get_config('okpay_account')) . '"> Example: OK123456789</td></tr>';
			echo '<tr><td>Account Email</td><td><input type="text" class="form-control" name="okpay_email" value="' . safe(get_config('okpay_email')) . '"></td></tr>';
			echo '<tr><td colspan="2" align="center"><input type="submit" class="btn btn-default" name="update" value="Update &raquo;"></td></tr>';
			echo '</table></form>';
		} else {
			if (empty($billic->errors)) {
				set_config('okpay_require_verification', $_POST['okpay_require_verification']);
				set_config('okpay_account', $_POST['okpay_account']);
				set_config('okpay_email', $_POST['okpay_email']);
				$billic->status = 'updated';
			}
		}
	}
